import React from 'react';

import './StoreViewChart.scss';

export default function StoreViewChart() {
    google.load("visualization", "1", { packages: ["corechart"] });
    google.setOnLoadCallback(drawChart2);
    function drawChart2() {
        var data = google.visualization.arrayToDataTable([
            ['Year', 'Sales', 'Expenses'],
            ['2013', 1000, 400],
            ['2014', 1170, 460],
            ['2015', 660, 1120],
            ['2016', 1030, 540]
        ]);

        var options = {
            title: 'Company Performance',
            hAxis: { title: 'Year', titleTextStyle: { color: '#333' } },
            vAxis: { minValue: 0 }
        };

        var chart = new google.visualization.AreaChart(document.getElementById('chart_div2'));
        chart.draw(data, options);
    }

    $(window).resize(function () {
        drawChart2();
    });

    // Reminder: you need to put https://www.google.com/jsapi in the head of your document or as an external resource on codepen //
    return (
        <>
            <div className="row">
                <div className="col-md-12 text-center">
                    <h1>Make Google charts responsive</h1>
                    <p>Full blog post details <a href="http://flopreynat.com/blog/make-google-charts-responsive.html">on my blog</a></p>
                </div>
                <div className="col-md-4 col-md-offset-4">
                    <hr />
                </div>
                <div className="clearfix"></div>
                <div className="col-md-6">
                    <div id="chart_div1" className="chart"></div>
                </div>
                <div className="col-md-6">
                    <div id="chart_div2" className="chart"></div>
                </div>
            </div>
        </>
    )
}
