import React, { useState } from "react";
import { Container } from "react-bootstrap";

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import rightBlockImage from '../Images/rightBlockImage.png'

import "./SignIn.scss";

export default function Maincontent() {

	const [emailValue,setEmailValue] = useState("");
	const [userPassword,setUserPassword] = useState("");

	const userData = (e) => {
		// [setEmailValue , setUserPassword ] = e.target.value;

		setEmailValue(e.target.value)
	}

	const signInFormSubmit = (e) => {
		console.log(emailValue);
		console.log(userPassword);
		e.preventDefault();
	}


	return (
		<div className="sign-in-page-main">
			<Container className="left-block">
				<div className="title-wrapper">
					<h2> Welcome Back</h2>
					<p> Enter your email and password to sign in</p>
				</div>
				<div className="form-wrapper">
					<Form onSubmit={signInFormSubmit}>
						<Form.Group className="input-items" controlId="formBasicEmail">
							<Form.Label>Email address</Form.Label>
							<Form.Control value={emailValue} onChange={userData} type="email" placeholder="Your email address" required/>
						</Form.Group>

						<Form.Group className="input-items" controlId="formBasicPassword">
							<Form.Label>Password</Form.Label>
							<Form.Control value={userPassword} onChange={userData} type="password" placeholder="Your password" required/>
						</Form.Group>
						<Form.Check 
							type="switch"
							id="custom-switch"
							label="Remember me"
						/>
						<Button className="btn-submit" variant="primary" type="submit">
							SIGN IN
						</Button>
						<Form.Text className="sign-up-link">
							Don't have an account? <a href="#">Sign up</a>
						</Form.Text>
					</Form>
				</div>
			</Container>
			<div className="right-block">
				<img src={ rightBlockImage }/>
			</div>
		</div>
	);
}
