import React, { useState } from "react";

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

export default function SignUpForm() {

    const [allValues, setAllValues] = useState({
        mobile: '',
        username: '',
        email: '',
        password: '',
        confirmPassword: ''
     });
    const [userName , setUserName] = useState("");

    const userDataHandler = (e) => {
        [username , email , password ] = e.target.value;
        setUserName(e.target.value);
    }

    const signUpFormSubmitHandler = () => {

    }

    return (
        <Form onSubmit={signUpFormSubmitHandler}>
            <Form.Group className="input-items" controlId="formBasicName">
                <Form.Label> Name </Form.Label>
                <Form.Control value= {userName} onChange={userDataHandler} type="text" placeholder="Your full name" required/>
            </Form.Group>

            <Form.Group className="input-items" controlId="formBasicEmail">
                <Form.Label>Email </Form.Label>
                <Form.Control type="email" onChange={userDataHandler} placeholder="Your email address" required/>
            </Form.Group>

            <Form.Group className="input-items" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" onChange={userDataHandler} placeholder="Your password" required/>
            </Form.Group>

            <Form.Check type="switch" id="custom-switch" label="Remember me" defaultChecked/>
            <Button className="btn-submit" variant="primary" type="submit">
                SIGN UP
            </Button>
            <Form.Text className="sign-in-link">
                Don't have an account? <a href="/signUp">Sign In</a>
            </Form.Text>
        </Form>
    )
}
